// GET: Fetch all books
export async function GET() {
  return NextResponse.json(books);
}

// POST: Add a new book
export async function POST(request: Request) {
  try {
    const { title, author, image, summary, orderStatus }: Partial<Book> = await request.json();

    if (!title || !author) {
      return NextResponse.json({ error: 'Title and Author are required' }, { status: 400 });
    }

    const newBook: Book = {
      id: Date.now(),
      title,
      author,
      image: image || '',
      summary: summary || '',
      orderStatus: orderStatus || 'For Sale',
    };

    books.push(newBook);
    return NextResponse.json(newBook, { status: 201 });
  } catch (error) {
    return NextResponse.json({ error: 'Failed to add book' }, { status: 500 });
  }
}

// PUT: Update a book by ID
export async function PUT(request: Request) {
  try {
    const { id, title, author, image, summary, orderStatus }: Partial<Book> = await request.json();

    if (!id) {
      return NextResponse.json({ error: 'Book ID is required' }, { status: 400 });
    }

    const bookIndex = books.findIndex((book) => book.id === id);

    if (bookIndex === -1) {
      return NextResponse.json({ error: 'Book not found' }, { status: 404 });
    }

    books[bookIndex] = {
      ...books[bookIndex],
      ...(title && { title }),
      ...(author && { author }),
      ...(image && { image }),
      ...(summary && { summary }),
      ...(orderStatus && { orderStatus }),
    };

    return NextResponse.json(books[bookIndex]);
  } catch (error) {
    return NextResponse.json({ error: 'Failed to update book' }, { status: 500 });
  }
}

// DELETE: Delete a book by ID
export async function DELETE(request: Request) {
  try {
    const { searchParams } = new URL(request.url);
    const id = Number(searchParams.get('id'));

    if (!id) {
      return NextResponse.json({ error: 'Book ID is required' }, { status: 400 });
    }

    const bookExists = books.some((book) => book.id === id);

    if (!bookExists) {
      return NextResponse.json({ error: 'Book not found' }, { status: 404 });
    }

    books = books.filter((book) => book.id !== id);
    return NextResponse.json({ message: 'Book deleted successfully' });
  } catch (error) {
    return NextResponse.json({ error: 'Failed to delete book' }, { status: 500 });
  }
}
